import React from 'react';

const CreatePost = () => {
    
    function onSubmit(event){
        event.preventDefault();

        const formData = new FormData(event.target);
        console.log(formData);

        const body = Object.fromEntries(formData.entries());

        if (body.title === '' || body.description === '')return alert('Please fill');
    }

    return (
        <form onSubmit= {onSubmit} className = "bg-white rounded-2xl mt-4 p-4 max-w-3xl w-full">
            <div className="flex flex-col text-sm">
                <label htmlFor="title">Titulo</label>
                <input type="text" name ="title" id="title" />
            </div>
            <div   div className= "flex flex-col text-sm">
                <label htmlFor="">Descripcion</label>
                <input type="text" name ="description" id="description" />
            </div>
            <div className = "flex flex-col text-sm">
                <label htmlFor="">Imagen</label>
                <input type="text" name ="image" id="image" />
            </div>
            <button type="submit" className="bg-blue-500 rounded-2xl mt-4 p-2">Publicar</button>
        </form>
    );
}

export default CreatePost;
