import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from './Post';

const PostContainer = () => {
    const [posts, setPosts ] = useState({
        status: 'loading',
        data: {},
    });

    useEffect(() => {
        async function getPosts () {
            const {data: response} = await axios.get('https://posts-pw2021.herokuapp.com/api/v1/post/all?limit=10&page=0', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
            });

            setPosts({status: 'done', data: response.data});
        }
        getPosts();
    
    }, []);

    if (posts.status === 'loading') {
        return <div>Cargando...</div>
    }

    return (
        <div className = "flex flex-wrap px-6 py-10 justify-center items center bg-black bg-opacity-30">
            {posts.data && posts.data.map((post) => <Post ke p={post}/>)}
        </div>
    );
};

export default PostContainer;
